@js:
result=JSON.parse(result)
config=getConfig()
result.paragraphs.map((p, i) => {
  function generateTran(i) {
    const chars = {
  	    baiduParagraphs: "ᵇ",
  	    youdaoParagraphs: "ʸ",
  	    gptParagraphs: "ᵍ",
  	    sakuraParagraphs: "ˢ"
    }
    if (config.translation==="并列") {
      return config.translationArr.map(
        t => result[t] ? (result[t][i]!="" ? chars[t] : "") + result[t][i] : ""
      ).join("\n")
    } else {
      for (let tt=0; tt<config.translationArr.length; tt++) {
        const t=config.translationArr[tt]
        if (result[t]) return (result[t][i]!="" ? chars[t] : "") + result[t][i]
      }
    }
  	}
  switch (String(config.language)) {
  	  case "日文":
  	    return p
  	  case "中文":
  	    return generateTran(i)
  	  case "中日":
  	    return [generateTran(i), p].join("\n")
  	  case "日中":
  	    return [p, generateTran(i)].join("\n")
  	}
}).join('\n')
