ruleToc.chapterList:
@js:
volumns=JSON.parse(String(java.getString('id.f-chapters-json@html')).replace(/<.?script[^<>]*>/gi,''))
volumns.forEach(volumn=> {
  volumn.children=[]
})
chapters=JSON.parse(String(java.getString('id.chapters-json@html')).replace(/<.?script[^<>]*>/gi,''))
chapters.forEach(chapter=>{
  volumns.forEach(volumn=> {
  	  if (chapter.parent_id===volumn.id)
  	    volumn.children.push(chapter)
  	})
})
result=[]
volumns.forEach(volumn => {
  result.push(JSON.stringify(volumn))
  volumn.children.forEach(chapter => {
    result.push(JSON.stringify(chapter))
  	})
})
//java.log(JSON.stringify(result))
result
